mapper.update:
update tb_common_board
set common_readcnt = common_readcnt + 1
where common_no = #{common_no}

mapper.select:
SELECT common_no, personal_id, member_type_id, common_title, common_content, TO_CHAR(COMMON_REGISTER_DATE,'YYYY-MM-DD HH24:MI:SS') AS common_register_date, common_readcnt
FROM tb_common_board
WHERE common_no = #{common_no}
